pragma solidity ^0.8.4;

contract Lottery {
    address public manager;
    address[] public players;

    modifier onlyManager {
        require(msg.sender == manager, "Not A Manager");
        _;
    }

    constructor() {
        manager = msg.sender;
    }

}
